import React from 'react'
import {Route,Redirect} from 'react-router-dom'
export default function PrivateRoute(props) {
    const isLogin = window.sessionStorage.getItem("username") ? true : false
    return (        
        isLogin ? <Route path={props.path} component={props.component}></Route> : 
        <Redirect to="/"></Redirect>   
    )
}
