import './App.css';
import 'antd/dist/antd.less'; // or 'antd/dist/antd.less'
import {BrowserRouter as Router,Route, Switch,Redirect} from 'react-router-dom';
import PrivateRoute from './components/public/PrivateRoute'
import router from './router/index'
import Login from '../src/views/login/Login'

function App() {
  let isLogin = window.sessionStorage.getItem("username") ? true : false
  return (
    <div className="App">
      <Router>
        <Switch>
            {
            router.map((item,index) => {
              return (
                item.auth ? <PrivateRoute key={index} path={item.path} component={item.com}></PrivateRoute>
                          :<Route path="/" component={item.com}></Route>

              )        
            })            
          }
        </Switch>
      </Router>
    </div>
  );
}

export default App;
